import './App.css';
import ScheduleSelector from 'react-schedule-selector'
import React from "react";


class App extends React.Component {

  // Hardcoded dates for the group availability
  hardcode1 = [new Date('1/1/22 9:00'), new Date('1/1/22 9:15'), new Date('1/1/22 9:30'), new Date('1/1/22 9:45'), new Date('1/1/22 10:00'), new Date('1/3/22 9:00'), new Date('1/3/22 9:15'), new Date('1/3/22 9:30'), new Date('1/3/22 9:45'), new Date('1/3/22 10:00'), new Date('1/4/22 11:00'), new Date('1/4/22 11:30'), new Date('1/4/22 12:00')]

  hardcode2 = [new Date('1/1/22 9:00'), new Date('1/1/22 9:15'), new Date('1/1/22 9:30'), new Date('1/1/22 9:45'), new Date('1/4/22 11:30'), new Date('1/4/22 12:00')]

  hardcode3 = [new Date('1/1/22 9:00'),  new Date('1/1/22 9:30'), new Date('1/1/22 9:45')]

  // more hardcoded dates for the group availability

  // hardcore4 = [new Date('10/3/22 13:00)', new Date('10/3/22 13:15'), new Date('10/3/22 13:30'), new Date('10/3/22 13:45'), new Date('10/3/22 14:00'), new Date('10/3/22 14:15'), new Date('10/3/22 14:30'), new Date('10/3/22 14:45'), new Date('10/3/22 15:00'), new Date('10/3/22 15:15'), new Date('10/3/22 15:30'), new Date('10/3/22 15:45'), new Date('10/3/22 16:00'), new Date('10/3/22 16:15'), new Date('10/3/22 16:30'), new Date('10/3/22 16:45'), new Date('10/3/22 17:00'), new Date('10/3/22 17:15'), new Date('10/3/22 17:30'), new Date('10/3/22 17:45'), new Date('10/3/22 18:00'), new Date('10/3/22 18:15'), new Date('10/3/22 18:30'), new Date('10/3/22 18:45'), new Date('10/3/22 19:00'), new Date('10/3/22 19:15'), new Date('10/3/22 19:30'), new Date('10/3/22 19:45'), new Date('10/3/22 20:00'), new Date('10/3/22 20:15'), new Date('10/3/22 20:30'), new Date('10/3/22 20:45'), new Date('10/3/22 21:00'), new Date('10/3/22 21:15'), new Date('10/3/22 21:30'), new Date('10/3/22 21:45'), new Date('10/3/22 22:00'), new Date('10/3/22 22:15'), new Date('10/3/22 22:30')]

  // hardcore5 = []


  // Initial State
  state = { schedule : []}

  // Handle the change in state
  handleChange = async newSchedule => {
    await this.setState({ schedule: newSchedule })
  }

  // Make the group availability side read-only
  doNothing = () => {
  }

  render() {
    return (<div className="container">
              <div className="relative">
                {/* Left side individual availabilities */}
                <span>Your Availabilities</span>
                <div className="overlap">
                  {/* Use Schedule Selector package */}
                  <ScheduleSelector
                    selection={this.state.schedule}
                    selectionScheme="linear"
                    numDays={5}
                    minTime={9}
                    maxTime={13}
                    hourlyChunks={4}
                    onChange={this.handleChange}
                    unselectedColor='rgba(195,61,158,0.05)'
                    selectedColor='rgba(195,61,158,1.0)'
                    hoveredColor='rgba(195,61,158,0.3)'
                    timeFormat='h:mm A'
                    startDate="1/1/22"
                  />
                </div>
              </div>
              {/* Right side group availabilities, we overlapped components to give gradient to the timeslots */}
              <span>Group Availabilities</span>
              <div className="relative">
                <div className="overlap" >
                  <ScheduleSelector
                    selection={this.hardcode1}
                    numDays={5}
                    minTime={9}
                    maxTime={13}
                    hourlyChunks={4}
                    onChange={this.doNothing}
                    unselectedColor='rgba(113,197,209,0.01)'
                    selectedColor='rgba(113,197,209,0.5)'
                    hoveredColor='rgba(113,197,209,0.1)'
                    timeFormat='h:mm A'
                    startDate="1/1/22"
                  />
                </div>
                <div className="overlap" >
                  <ScheduleSelector
                    selection={this.hardcode2}
                    numDays={5}
                    minTime={9}
                    maxTime={13}
                    hourlyChunks={4}
                    onChange={this.doNothing}
                                        selectionScheme="linear"

                    unselectedColor='rgba(113,197,209,00.1)'
                    selectedColor='rgba(113,197,209,0.5)'
                    hoveredColor='rgba(113,197,209,0.1)'
                    timeFormat='h:mm A'
                    startDate="1/1/22"
                  />
                </div>
                <div className="overlap" >
                  <ScheduleSelector
                    selection={this.hardcode3}
                    numDays={5}
                    minTime={9}
                    maxTime={13}
                    hourlyChunks={4}
                    onChange={this.doNothing}
                    unselectedColor='rgba(113,197,209,0.01)'
                    selectedColor='rgba(113,197,209,0.5)'
                    hoveredColor='rgba(113,197,209,0.1)'
                    timeFormat='h:mm A'
                    startDate="1/1/22"
                  />
                </div>
                <div className="overlap">
                  <ScheduleSelector
                    selection={this.state.schedule}
                    numDays={5}
                    minTime={9}
                    maxTime={13}
                    hourlyChunks={4}
                    onChange={this.doNothing}
                    unselectedColor='rgba(113,197,209,0.01)'
                    selectedColor='rgba(113,197,209,0.5)'
                    hoveredColor='rgba(113,197,209,0.0)'
                    timeFormat='h:mm A'
                    startDate="1/1/22"
                  />
                </div>
              </div>
            </div>
    )
  }
}

export default App;
